mapper.select:

SELECT book_id, book_name, book_price, book_des, book_img, book_pages, book_isbn, book_writer, book_regdate, category_code
FROM book_list
WHERE book_id = #{num}


SELECT book_id, book_name, book_price, book_des, book_img, book_pages, book_isbn, book_writer, book_regdate, category_code
FROM book_list
WHERE category_code = #{category}
AND book_isbn = #{isbn}
ORDER BY book_id DESC

mapper.update:

UPDATE book_list
SET book_isbn = 'N'
WHERE book_id = #{num};
